import "core-js/modules/es.array.push.js";
import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.find.js";
import axios from 'axios';
import { useStore } from 'vuex';
import { computed, onMounted, ref } from 'vue';
import { useRouter, useRoute } from 'vue-router';
export default {
  name: 'PaymentPage',
  setup() {
    const store = useStore();
    const router = useRouter();
    const route = useRoute();
    const plans = ref([{
      id: 1,
      name: 'Basic Plan',
      price: 199,
      duration: 30
    }, {
      id: 2,
      name: 'Standard Plan',
      price: 499,
      duration: 90
    }, {
      id: 3,
      name: 'Premium Plan',
      price: 999,
      duration: 365
    }]);
    const selectedPlan = ref(null);
    const showPaymentForm = ref(false);

    // Computed properties
    const user = computed(() => store.state.user);
    const isAuthenticated = computed(() => store.state.isAuthenticated);
    const loadRazorpay = () => {
      return new Promise((resolve, reject) => {
        const script = document.createElement('script');
        script.src = 'https://checkout.razorpay.com/v1/checkout.js';
        script.onload = resolve;
        script.onerror = () => reject(new Error('Failed to load Razorpay SDK'));
        document.body.appendChild(script);
      });
    };
    onMounted(async () => {
      try {
        // Load Razorpay SDK
        await loadRazorpay();
        console.log('Razorpay SDK loaded successfully');
      } catch (error) {
        console.error('Failed to load Razorpay:', error);
        alert('Unable to initialize payment system. Please refresh the page.');
        return;
      }

      // Check for any pending payment verification
      const localData = localStorage.getItem('payment_success');
      const sessionData = sessionStorage.getItem('payment_success');
      const paymentData = localData || sessionData;
      if (paymentData) {
        const parsedData = JSON.parse(paymentData);

        // If payment was verified in backend but app crashed
        if (parsedData.backendVerified && !parsedData.verified) {
          try {
            // Double-check subscription status with backend
            const checkResponse = await axios.post('/api/payment/check-subscription/', {
              subscription_id: parsedData.subscriptionId
            });
            if (checkResponse.data.status === 'active') {
              // Clear payment data and redirect to profile
              localStorage.removeItem('payment_success');
              sessionStorage.removeItem('payment_success');
              router.replace('/profile');
              return;
            }
          } catch (err) {
            console.error('Error checking subscription:', err);
          }
        }
      }
      console.log('Payment Component Mounted:', {
        user: user.value,
        isAuthenticated: isAuthenticated.value,
        route: route.fullPath
      });
      if (!isAuthenticated.value || !user.value) {
        console.log('User not authenticated, redirecting to login');
        router.push({
          path: '/login',
          query: {
            redirect: route.fullPath
          }
        });
        return;
      }

      // Check for plan in route query
      const planType = route.query.plan;
      if (planType) {
        const plan = plans.value.find(p => p.name.toLowerCase().replace(' ', '_') === planType);
        if (plan) {
          selectPlan(plan);
        }
      }
    });
    const selectPlan = plan => {
      console.log('Selected Plan:', plan);
      selectedPlan.value = plan;
      showPaymentForm.value = true;
    };
    const initiatePayment = async () => {
      try {
        // Check if Razorpay is loaded
        if (!window.Razorpay) {
          throw new Error('Payment gateway not initialized. Please refresh the page.');
        }

        // Validate plan selection
        if (!selectedPlan.value || !selectedPlan.value.price) {
          throw new Error('Please select a valid plan.');
        }
        console.log('Initiating payment for plan:', selectedPlan.value);
        const orderResponse = await axios.post('/api/payment/create-order/', {
          amount: selectedPlan.value.price,
          currency: 'INR'
        });
        console.log('Order created:', orderResponse.data);
        const options = {
          key: orderResponse.data.key_id,
          amount: orderResponse.data.amount,
          currency: orderResponse.data.currency,
          name: 'Your OTT Platform',
          description: `${selectedPlan.value.name} Subscription`,
          order_id: orderResponse.data.order_id,
          handler: handlePaymentSuccess,
          prefill: {
            name: user.value?.name || '',
            email: user.value?.email || '',
            contact: user.value?.phone || ''
          },
          theme: {
            color: '#3399cc'
          },
          // Add callback URLs for UPI app redirection with fast redirect
          callback_url: `${window.location.origin}/payment/verify`,
          redirect: true,
          retry: false,
          // Disable retry to speed up redirect
          modal: {
            confirm_close: false,
            // Disable confirmation dialog
            escape: false,
            // Prevent escape key from closing
            animation: false // Disable animations for faster response
          }
        };
        const rzp = new window.Razorpay(options);
        rzp.open();
      } catch (error) {
        console.error('Payment initiation failed:', error);
        alert('Unable to initiate payment. Please try again.');
      }
    };
    const handlePaymentSuccess = async response => {
      try {
        console.log('Payment successful, verifying:', response);
        const verifyResponse = await axios.post('/api/payment/verify-payment/', {
          razorpay_payment_id: response.razorpay_payment_id,
          razorpay_order_id: response.razorpay_order_id,
          razorpay_signature: response.razorpay_signature
        });
        if (verifyResponse.data.status === 'success') {
          // Store payment success in localStorage for UPI app return
          const verificationId = Math.random().toString(36).substring(7);
          const paymentData = {
            orderId: response.razorpay_order_id,
            timestamp: new Date().getTime(),
            verificationId: verificationId,
            verified: false,
            amount: response.amount,
            plan: selectedPlan.value.name,
            backendVerified: true,
            // Backend verification flag
            subscriptionId: verifyResponse.data.subscription_id,
            // Store subscription ID
            activationTime: verifyResponse.data.activation_time
          };

          // Store in both localStorage and sessionStorage for redundancy
          localStorage.setItem('payment_success', JSON.stringify(paymentData));
          sessionStorage.setItem('payment_success', JSON.stringify(paymentData));

          // For non-UPI payments or when not redirecting
          if (!response.isUpi) {
            alert('Payment successful! Your subscription is now active.');
            router.push('/profile');
          }
        }
      } catch (error) {
        console.error('Payment verification failed:', error);
        alert('Payment verification failed. Please contact support.');
      }
    };
    return {
      plans,
      selectedPlan,
      showPaymentForm,
      selectPlan,
      initiatePayment
    };
  }
};
