      }
      if (typeof duration === 'string' && duration.includes(':')) {
        const parts = duration.split(':').map(Number);
        let sec = 0;
        if (parts.length === 3) {
          sec = parts[0] * 3600 + parts[1] * 60 + parts[2];
        } else if (parts.length === 2) {
          sec = parts[0] * 60 + parts[1];
